    <style>
         :root {
            --bgr-color: #504f4f73;
            --main-color: #191F3A;
            --sub-color: #EAA023;
            --text-color: #ffffff;
            --bg-color: #0C1023;
            --bd-color: #ffffff91;
            --bl-color: black;
        }
        body{background-color: var(--bg-color);}
        h1{text-align: center; color: var(--sub-color); margin-top: 50px; padding-bottom: 10px; border-bottom: 1px solid var(--bd-color);}
        h1>a{color: var(--sub-color); }
        h2{ margin: 50px 85% 0 50px; padding-bottom: 10px; color: var(--text-color); border-bottom: 3px solid var(--text-color);}
        a{text-decoration: none;}
        img{width: 180px; height: 200px;
            border-radius: 10px;
            transition: all 0.3s;}
        img:hover{ width: 182px; height: 202px;}
        .add-link{color: var(--sub-color);}

        #table-list{
            display: flex; margin: 20px 30px 20px 30px; justify-content: space-around; height: 300px;
        }
        .table-list-item{
            height: 250px; transition: all 0.3s; margin-top: 20px;
        }
        .table-list-item>figcaption{color: var(--text-color); padding: 10px 0  10px 5px;}
        .table-list-item:hover{background-color: var(--main-color);  border-radius: 5px; }
        figcaption:hover{color: rgba(238, 130, 238, 0.8);}

        /* cane-list */
        #cane-list{display: flex; margin: 20px 30px 20px 30px; justify-content: space-around; height: 300px;}
        .cane-list-item{height: 250px; transition: all 0.3s; margin-top: 20px;}
        .cane-list-item>figcaption{color: var(--text-color); padding: 10px 0  0 5px;}
        .cane-list-item:hover{background-color: var(--main-color); border-radius: 5px;}
        figcaption:hover{color: rgba(238, 130, 238, 0.8);}

        /* accessory-list: phụ kiện bida */
        #accessory-list{
            height: 300px;
            display: flex;
            margin: 20px 30px 20px 30px;
            justify-content: space-around;
        }
        .accessory-list-item>figcaption{color: var(--text-color); padding: 10px 0  0 5px;}
        .accessory-list-item{height: 250px; transition: all 0.3s; margin-top: 20px;}
        .accessory-list-item:hover{background-color: var(--main-color); border-radius: 5px;}
        figcaption:hover{color: rgba(238, 130, 238, 0.8);}

    </style>
